import React, { Component } from 'react'

import Board from './Board';

export default class Game extends Component {
    constructor(props) {
        super(props);
        this.state = {
          squares: Array(9).fill(null),
        }
        }
        handleClick(i) {
            const squares = this.state.squares.slice();
            squares[i] = 'X';
            this.setState({squares: squares});
          }
          
}
